CREATE TABLE EVENTO (id_evento BIGINT NOT NULL, contacto_evento VARCHAR(20) NOT NULL, descripcion VARCHAR(60) NOT NULL, fecha_fin TIMESTAMP, fecha_inicio TIMESTAMP NOT NULL, localizacion VARCHAR(40) NOT NULL, tipo_evento VARCHAR(9) NOT NULL, usuario BIGINT, PRIMARY KEY (id_evento))
CREATE TABLE COMENTARIO (id_comentario BIGINT NOT NULL, cuerpo VARCHAR(200) NOT NULL, titulo VARCHAR(20) NOT NULL, usuario BIGINT NOT NULL, PRIMARY KEY (id_comentario))
CREATE TABLE FOTO (id_foto BIGINT NOT NULL, nombre VARCHAR(20) NOT NULL, path VARCHAR(40) NOT NULL, id_usuario BIGINT NOT NULL, PRIMARY KEY (id_foto))
CREATE TABLE USUARIO (id_usuario BIGINT NOT NULL, correoE VARCHAR(20) NOT NULL, password VARCHAR(20) NOT NULL, rol VARCHAR(10) NOT NULL, telefono BIGINT, PRIMARY KEY (id_usuario))
CREATE TABLE VALORACION (valor INTEGER, id_evento INTEGER NOT NULL, id_usuario INTEGER NOT NULL, id_valoracion INTEGER NOT NULL, valEvento VARCHAR(60), valUsuario BIGINT, PRIMARY KEY (id_evento, id_usuario, id_valoracion))
CREATE TABLE EVENTO_COMENTARIO (Evento_id_evento BIGINT NOT NULL, comentarios_id_comentario BIGINT NOT NULL, PRIMARY KEY (Evento_id_evento, comentarios_id_comentario))
CREATE TABLE EVENTO_FOTO (Evento_id_evento BIGINT NOT NULL, fotos_id_foto BIGINT NOT NULL, PRIMARY KEY (Evento_id_evento, fotos_id_foto))
ALTER TABLE EVENTO ADD CONSTRAINT FK_EVENTO_usuario FOREIGN KEY (usuario) REFERENCES USUARIO (id_usuario)
ALTER TABLE COMENTARIO ADD CONSTRAINT COMENTARIO_usuario FOREIGN KEY (usuario) REFERENCES USUARIO (id_usuario)
ALTER TABLE FOTO ADD CONSTRAINT FK_FOTO_id_usuario FOREIGN KEY (id_usuario) REFERENCES USUARIO (id_usuario)
ALTER TABLE VALORACION ADD CONSTRAINT VLORACIONvlUsuario FOREIGN KEY (valUsuario) REFERENCES USUARIO (id_usuario)
ALTER TABLE VALORACION ADD CONSTRAINT VLORACIONvalEvento FOREIGN KEY (valEvento) REFERENCES EVENTO (descripcion)
ALTER TABLE EVENTO_COMENTARIO ADD CONSTRAINT VNTCMNcmntrsdcmntr FOREIGN KEY (comentarios_id_comentario) REFERENCES COMENTARIO (id_comentario)
ALTER TABLE EVENTO_COMENTARIO ADD CONSTRAINT VNTCMNTRIOvntdvnto FOREIGN KEY (Evento_id_evento) REFERENCES EVENTO (id_evento)
ALTER TABLE EVENTO_FOTO ADD CONSTRAINT VNTFOTOvntidevento FOREIGN KEY (Evento_id_evento) REFERENCES EVENTO (id_evento)
ALTER TABLE EVENTO_FOTO ADD CONSTRAINT VNTOFOTOftosidfoto FOREIGN KEY (fotos_id_foto) REFERENCES FOTO (id_foto)
